import * as i0 from "@angular/core";
import * as i1 from "../supabase.service";
export class AccountComponent {
    constructor(supabase) {
        this.supabase = supabase;
        this.loading = false;
    }
    ngOnInit() {
        this.getProfile();
    }
    async getProfile() {
        try {
            this.loading = true;
            let { data: profile, error, status } = await this.supabase.profile;
            if (error && status !== 406) {
                throw error;
            }
            if (profile) {
                this.profile = profile;
            }
        }
        catch (error) {
            alert(error.message);
        }
        finally {
            this.loading = false;
        }
    }
    async updateProfile(username, website, avatar_url = '') {
        try {
            this.loading = true;
            await this.supabase.updateProfile({ username, website, avatar_url });
        }
        catch (error) {
            alert(error.message);
        }
        finally {
            this.loading = false;
        }
    }
    async signOut() {
        await this.supabase.signOut();
    }
}
